import React, { Component } from 'react'

class IncrementDecrement extends Component {

    constructor(props) {
        super(props)

        this.state = {
             count : 0
        }
    }

    incrementCount = () => {
        this.setState(prevState => {
            return {icount : prevState.count + 1}
        })
    }

    decrementCount = () => {
        this.setState(newState => {
            return {dcount : newState.count -1}
        })
    }

    render() {
        const {icount} = this.state
        const {dcount} = this.state
        return (
            <button onClick={this.incrementCount}>Increase</button>,
            <h2>{icount}</h2>,
            <h2>{dcount}</h2>,
            <button onClick={this.decrementCount}>Increase</button>
        )
    }
}

export default IncrementDecrement
