import React, { Component } from 'react'

class RefsDemo extends Component {
    constructor(props) {
        super(props)
        this.inputRef = Reac.createRef()
    }

    componentDidMount() {
        console.log(this);
    }

    render() {
        return (
            <div>
                <input type="text" ref={this.inputRef} placeholder="Write here" />
            </div>
        )
    }
}

export default RefsDemo
