import React, { Component } from 'react'

class Form extends Component {

    constructor(props) {
        super(props)

        this.state = {   
             username : '',
             comments : '',
             topic : ''
        }
    }

    handleUsernameChange = (event) => {
        this.setState({
            username : event.target.value
        })
    }

    handleCommentsChange = (event)=> {
        this.setState({
            comments: event.target.value
        })
    }

    render() {
        return (
            <form>
                <div>   
                    <label>Username</label>
                    <input type="text" autoFocus value= {this.state.username} onChange={this.handleUsernameChange} />
                </div>
                <div>
                    <label>Comments</label>
                    {/* <textarea type="text" value={this.state.comments} onchange={this.handleCommentsChange} /> */}
                    <textarea type="text" value={this.state.comments} onChange={this.handleCommentsChange} placeholder='enter comments'/>
                </div>
                <div>
                    <lable>Topic</lable>
                    <select value>
                        <option value="react">React</option>
                        <option value="react">Angular</option>
                        <option value="react">Vue</option>
                    </select>
                </div>
            </form>
        )
    }
}

export default Form
