import React, { Component } from 'react'

class IncrementDecrement extends Component {

    constructor(props) {
        super(props)

        this.state = {
             count : 0
        }
    }

    incrementCount = () => {
        this.setState(prevState => {
            return {count : prevState.icount + 1}
        })
    }

    decrementCount = () => {
        this.setState(newState => {
            return {count : newState.dcount -1}
        })
    }


    render() {
        const {icount} = this.state
        const {dcount} = this.state
        return (
            <>
                {icount}
                <button onClick={this.incrementCount}>Increase</button>
                <button onClick={this.decrementCount}>dicrease</button>
            </>
        )
    }
}

export default IncrementDecrement
